CREATE TABLE stock_record (
                              id INT AUTO_INCREMENT PRIMARY KEY, -- 自增主键
                              buy_five VARCHAR(20),              -- 买五数量
                              buy_five_pri VARCHAR(20),          -- 买五价格
                              buy_four VARCHAR(20),              -- 买四数量
                              buy_four_pri VARCHAR(20),          -- 买四价格
                              buy_one VARCHAR(20),               -- 买一数量
                              buy_one_pri VARCHAR(20),           -- 买一价格
                              buy_three VARCHAR(20),             -- 买三数量
                              buy_three_pri VARCHAR(20),         -- 买三价格
                              buy_two VARCHAR(20),               -- 买二数量
                              buy_two_pri VARCHAR(20),           -- 买二价格
                              competitive_pri VARCHAR(20),       -- 竞价价格
                              `date` DATE,                       -- 日期
                              gid VARCHAR(20),                   -- 股票代码
                              incre_per VARCHAR(20),             -- 涨跌百分比
                              increase VARCHAR(20),              -- 涨跌金额
                              `name` VARCHAR(50),                -- 股票名称
                              now_pri VARCHAR(20),               -- 当前价格
                              reserve_pri VARCHAR(20),           -- 保留价格
                              sell_five VARCHAR(20),             -- 卖五数量
                              sell_five_pri VARCHAR(20),         -- 卖五价格
                              sell_four VARCHAR(20),             -- 卖四数量
                              sell_four_pri VARCHAR(20),         -- 卖四价格
                              sell_one VARCHAR(20),              -- 卖一数量
                              sell_one_pri VARCHAR(20),          -- 卖一价格
                              sell_three VARCHAR(20),            -- 卖三数量
                              sell_three_pri VARCHAR(20),        -- 卖三价格
                              sell_two VARCHAR(20),              -- 卖二数量
                              sell_two_pri VARCHAR(20),          -- 卖二价格
                              `time` TIME,                       -- 时间
                              today_max VARCHAR(20),             -- 今日最高价
                              today_min VARCHAR(20),             -- 今日最低价
                              today_start_pri VARCHAR(20),       -- 今日开盘价
                              tra_amount VARCHAR(50),            -- 成交金额
                              tra_number VARCHAR(20),            -- 成交数量
                              yestod_end_pri VARCHAR(20),        -- 昨日收盘价
                              dot VARCHAR(20),                   -- 当前点数
                              now_pic VARCHAR(20),               -- 当前涨跌图标
                              rate VARCHAR(20),                  -- 涨跌率
                              tra_amount_pan VARCHAR(50),        -- 大盘成交金额
                              tra_number_pan VARCHAR(20),        -- 大盘成交数量
                              minurl TEXT,                       -- 分时图 URL
                              dayurl TEXT,                       -- 日 K 线图 URL
                              weekurl TEXT,                      -- 周 K 线图 URL
                              monthurl TEXT                      -- 月 K 线图 URL
);


